mapper.select:
select id, account, password, username
from user_system.user
where id = #{id}

select account, username, email
from user_system.user
where account = #{account}
  and password = #{password}

mapper.update:
update user_system.user
set account  = #{account},
    username = #{username},
    password = #{password}
where id = #{id}

UPDATE user_system.user


    password = #{newPassword},


    username = #{username},


    email = #{email}


WHERE account = #{account}
AND password = #{password}
