mapper.select:
select id, account, password, username
from user_system.user
where id = #{id}

select account, username, email
from user_system.user
where account = #{account}
  and password = #{password}

mapper.update:
update user_system.user
set account  = #{account},
    username = #{username},
    password = #{password}
where id = #{id}

update user_system.user
set account  = #{account},
    username = #{username},
    password = #{newPassword}
where account = #{account}
  and password = #{password}
